"use strict";class FooterAccordion{constructor(t,e){this.listOfElements={itemArr:t,classStr:e},this.mainFunc(),console.log(this)}mainFunc(t){(t=this.listOfElements).itemArr.forEach(e=>{e.setAttribute("data-opened","0"),e.addEventListener("click",function(){"0"==e.getAttribute("data-opened")?(t.itemArr.forEach(e=>{e.classList.remove(t.classStr),e.setAttribute("data-opened","0")}),e.classList.add(t.classStr),e.setAttribute("data-opened","1")):(e.classList.remove(t.classStr),e.setAttribute("data-opened","0"))})})}}export default FooterAccordion;