class SearchPopup{constructor(t,e,s){this.listOfElements={eventEl:t,targetEl:e,classStr:s},this.mainFunc()}mainFunc(t){(t=this.listOfElements).eventEl.addEventListener("click",function(){"0"==t.eventEl.getAttribute("data-opened")||null==t.eventEl.getAttribute("data-opened")?(t.targetEl.classList.add(t.classStr),t.eventEl.setAttribute("data-opened","1")):(t.eventEl.setAttribute("data-opened","0"),t.eventEl.classList.remove(t.classStr))})}}